import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Card, CardHeader, CardContent } from './components/ui/card';
import { Button } from './components/ui/button';
import { Alert, AlertDescription } from './components/ui/alert';
import { Building, Wallet, ArrowLeft, AlertCircle, Loader2, DollarSign, MapPin } from 'lucide-react';
import PropertyForm from './components/ui/PropertyForm';
import { ScrollArea } from './components/ui/scroll-area';

import { 
    initializeWeb3, 
    initializeContract, 
    connectWallet, 
    switchToHardhatNetwork, 
    formatPrice 
} from './utilsApp/web3';

import { displayErrorMessage } from './utilsApp/errors';

const RealEstateApp = () => {
    const navigate = useNavigate();
    const [isLoading, setIsLoading] = useState(true);
    const [isProcessing, setIsProcessing] = useState(false);
    const [account, setAccount] = useState('');
    const [contract, setContract] = useState(null);
    const [web3Instance, setWeb3Instance] = useState(null);
    const [myProperties, setMyProperties] = useState([]);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [connectionStatus, setConnectionStatus] = useState('Initializing...');
    const [showListingForm, setShowListingForm] = useState(false);

    const initializeBlockchain = async () => {
        setIsLoading(true);
        setError('');
        
        try {
            setConnectionStatus('Initializing Web3...');
            const web3 = await initializeWeb3();
            setWeb3Instance(web3);

            const accounts = await web3.eth.getAccounts();
            if (accounts.length > 0) {
                setAccount(accounts[0]);
                await loadMyProperties(accounts[0], web3);
            }

            setConnectionStatus('Checking network...');
            const chainId = await web3.eth.getChainId();
            
            if (chainId !== 31337) {
                setConnectionStatus('Switching to Hardhat network...');
                await switchToHardhatNetwork();
            }

            setConnectionStatus('Initializing contract...');
            const contractInstance = await initializeContract(web3);
            setContract(contractInstance);
            setConnectionStatus('Connected');
            
        } catch (err) {
            setError(displayErrorMessage(err, 'Initialization Error'));
            setConnectionStatus('Connection failed');
        } finally {
            setIsLoading(false);
        }
    };

    const loadMyProperties = async (currentAccount, web3) => {
        try {
            const contractInstance = await initializeContract(web3);
            const properties = await contractInstance.methods.getAllProperties().call();
            
            const myProps = properties.filter(prop => 
                prop.owner.toLowerCase() === currentAccount.toLowerCase()
            ).map(prop => ({
                ...prop,
                price: formatPrice(web3, prop.price)
            }));
            
            setMyProperties(myProps);
        } catch (err) {
            setError('Failed to load your properties');
        }
    };

    const handlePropertySubmit = async (propertyData) => {
        setIsProcessing(true);
        setError('');
        setSuccess('');
    
        try {
            if (!contract || !account || !web3Instance) {
                throw new Error('Please connect your wallet before listing a property');
            }
    
            // Check if property ID already exists
            const properties = await contract.methods.getAllProperties().call();
            const propertyExists = properties.some(prop => 
                prop.id.toLowerCase() === propertyData.id.toLowerCase()
            );
            
            if (propertyExists) {
                setError('This Property ID is already in use. Please choose a different unique identifier for your property listing.');
                return;
            }
    
            // Image validation
            if (!propertyData.image) {
                setError('Please upload an image for your property listing');
                return;
            }
    
            // Convert price to Wei
            const priceInWei = web3Instance.utils.toWei(propertyData.price.toString(), 'ether');
    
            // Store image data (in a real application, you'd upload to IPFS or similar)
            const propertyWithImage = {
                ...propertyData,
                image: propertyData.image
            };
    
            // Create property on blockchain
            await contract.methods.createProperty(
                propertyData.id,
                propertyData.title,
                propertyData.description,
                priceInWei,
                propertyData.location,
                [propertyData.image] // Store image URL/hash in documents array
            ).send({
                from: account,
                gas: 500000
            });
    
            await loadMyProperties(account, web3Instance);
            setSuccess('Your property has been successfully listed!');
            setShowListingForm(false);
    
        } catch (err) {
            if (err.message.includes('User denied')) {
                setError('Transaction was cancelled. Please try again if you want to list your property.');
            } else if (err.message.includes('insufficient funds')) {
                setError('Your wallet has insufficient funds to complete this transaction.');
            } else if (err.message.includes('gas')) {
                setError('Transaction failed due to gas estimation. Please try again with a different price.');
            } else {
                setError('Failed to list your property. Please check your inputs and try again.');
            }
            console.error('Property listing error:', err);
        } finally {
            setIsProcessing(false);
        }
    };

    useEffect(() => {
        initializeBlockchain();
        
        if (window.ethereum) {
            window.ethereum.on('accountsChanged', handleAccountsChanged);
            window.ethereum.on('chainChanged', () => window.location.reload());
        }

        return () => {
            if (window.ethereum) {
                window.ethereum.removeListener('accountsChanged', handleAccountsChanged);
                window.ethereum.removeListener('chainChanged', () => window.location.reload());
            }
        };
    }, []);

    const handleAccountsChanged = async (accounts) => {
        if (accounts.length > 0) {
            setAccount(accounts[0]);
            await loadMyProperties(accounts[0], web3Instance);
        } else {
            setAccount('');
            setMyProperties([]);
            setError('Please connect your wallet');
        }
    };

    if (isLoading) {
        return (
            <div className="container mx-auto p-4 min-h-screen flex items-center justify-center">
                <div className="text-center">
                    <Loader2 className="h-8 w-8 animate-spin mx-auto mb-4" />
                    <h2 className="text-xl font-semibold mb-2">Connecting to Blockchain</h2>
                    <p className="text-gray-600">{connectionStatus}</p>
                </div>
            </div>
        );
    }

    return (
        <div className="container mx-auto p-4 min-h-screen">
            <Card className="mb-6">
                <CardHeader>
                    <div className="flex justify-between items-center flex-wrap gap-4">
                        <div className="flex items-center gap-4">
                            <Button 
                                onClick={() => navigate('/menu')} 
                                variant="outline"
                                className="flex items-center gap-2"
                            >
                                <ArrowLeft className="h-4 w-4" />
                                Back to Menu
                            </Button>
                            <h1 className="text-2xl font-bold">Sell Your Property</h1>
                        </div>
                        <div className="flex gap-2 flex-wrap">
                            <Button 
                                onClick={async () => {
                                    try {
                                        const address = await connectWallet();
                                        setAccount(address);
                                        await loadMyProperties(address, web3Instance);
                                    } catch (err) {
                                        setError(displayErrorMessage(err, 'Wallet Connection Error'));
                                    }
                                }}
                                disabled={isProcessing}
                                className="flex items-center gap-2"
                            >
                                <Wallet className="h-4 w-4" />
                                {account ? `${account.slice(0, 6)}...${account.slice(-4)}` : 'Connect Wallet'}
                            </Button>
                        </div>
                    </div>
                </CardHeader>
            </Card>

            {error && (
                <Alert variant="destructive" className="mb-6">
                    <AlertCircle className="h-4 w-4" />
                    <AlertDescription className="ml-2">{error}</AlertDescription>
                </Alert>
            )}

            {success && (
                <Alert className="mb-6 bg-green-50 border-green-200">
                    <AlertDescription className="text-green-800">{success}</AlertDescription>
                </Alert>
            )}

            <div className="grid md:grid-cols-2 gap-6">
                <Card>
                    <CardHeader>
                        <h2 className="text-xl font-semibold">List New Property</h2>
                    </CardHeader>
                    <CardContent>
                        <PropertyForm 
                            onSubmit={handlePropertySubmit}
                              contract={contract}
                                isProcessing={isProcessing}
                                web3Instance={web3Instance}
                                account={account}
                        />
                    </CardContent>
                </Card>

                <Card>
                    <CardHeader>
                        <h2 className="text-xl font-semibold">My Listed Properties</h2>
                    </CardHeader>
                    <CardContent>
                        <ScrollArea className="h-[600px]">
                            {myProperties.length === 0 ? (
                                <div className="text-center py-8">
                                    <Building className="h-12 w-12 mx-auto text-gray-400 mb-4" />
                                    <p className="text-gray-600">No properties listed yet</p>
                                </div>
                            ) : (
                                <div className="space-y-4">
                                    {myProperties.map((property, index) => (
                                        <Card key={index} className="p-4">
                                            <div className="space-y-2">
                                                <h3 className="font-semibold">{property.title}</h3>
                                                <p className="text-sm text-gray-600">ID: {property.id}</p>
                                                <p className="text-sm text-gray-600">{property.description}</p>
                                                <div className="flex items-center gap-2 text-sm">
                                                    <MapPin className="h-4 w-4" />
                                                    {property.location}
                                                </div>
                                                <div className="flex items-center gap-2 text-sm font-medium">
                                                    <DollarSign className="h-4 w-4" />
                                                    {property.price} ETH
                                                </div>
                                                <div className="flex items-center gap-2">
                                                    <span className={`px-2 py-1 rounded-full text-xs ${
                                                        property.isActive 
                                                            ? 'bg-green-100 text-green-700' 
                                                            : 'bg-red-100 text-red-700'
                                                    }`}>
                                                        {property.isActive ? 'Active' : 'Sold'}
                                                    </span>
                                                </div>
                                            </div>
                                        </Card>
                                    ))}
                                </div>
                            )}
                        </ScrollArea>
                    </CardContent>
                </Card>
            </div>
        </div>
    );
};

export default RealEstateApp;
