// src/real-estate-package/utilsApp/errors.js

export const validatePropertyFormData = (property) => {
    const errors = {};

    if (!property.id || !property.id.trim()) {
        errors.id = 'Property ID is required';
    }

    if (!property.title || !property.title.trim()) {
        errors.title = 'Property title is required';
    }

    if (!property.description || !property.description.trim()) {
        errors.description = 'Property description is required';
    }

    if (!property.location || !property.location.trim()) {
        errors.location = 'Property location is required';
    }

    if (!property.price || isNaN(property.price) || parseFloat(property.price) <= 0) {
        errors.price = 'Please enter a valid price greater than 0';
    }

    if (Object.keys(errors).length > 0) {
        const error = new Error('Validation failed');
        error.errors = errors;
        throw error;
    }

    return true;
};

export const handleWeb3Error = (error) => {
    if (typeof error === 'string') {
        return error;
    }

    if (error.message) {
        // Handle MetaMask specific errors
        if (error.message.includes('User denied transaction')) {
            return 'Transaction was cancelled';
        }
        if (error.message.includes('insufficient funds')) {
            return 'Insufficient funds to complete this transaction';
        }
        if (error.message.includes('nonce too low')) {
            return 'Transaction error: Please reset your MetaMask account';
        }
        return error.message;
    }

    return 'An unexpected error occurred';
};
