import React, { useState } from 'react';
import { Card, CardHeader, CardContent } from './card';
import { Button } from './button';
import { Input } from './input';
import { Textarea } from './textarea';
import { Plus, Loader2, ChevronDown } from 'lucide-react';
import { validatePropertyFormData } from '../../utilsApp/errors';

// Constants from our error handling utilities
const ISRAELI_CITIES = [
    'Jerusalem', 'Tel Aviv', 'Haifa', 'Rishon LeZion',
    'Petah Tikva', 'Ashdod', 'Netanya', 'Beer Sheva',
    'Holon', 'Bnei Brak'
];

const PropertyForm = ({ onSubmit, contract, isProcessing }) => {
    const [showCities, setShowCities] = useState(false);
    const [formErrors, setFormErrors] = useState({});
    const [property, setProperty] = useState({
        id: '',
        title: '',
        description: '',
        location: '',
        price: ''
    });

    const handleSubmit = async (e) => {
        e.preventDefault();
        const errors = validatePropertyFormData(property);
        
        if (Object.keys(errors).length === 0) {
            try {
                await onSubmit(property);
                // Clear form on successful submission
                setProperty({
                    id: '',
                    title: '',
                    description: '',
                    location: '',
                    price: ''
                });
                setFormErrors({});
            } catch (error) {
                setFormErrors({ submit: error.message });
            }
        } else {
            setFormErrors(errors);
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        
        // Special handling for description to limit lines
        if (name === 'description') {
            const lines = value.split('\n');
            if (lines.length <= 4) {
                setProperty(prev => ({ ...prev, [name]: value }));
            }
        } else {
            setProperty(prev => ({ ...prev, [name]: value }));
        }
        
        // Clear error when user starts typing
        if (formErrors[name]) {
            setFormErrors(prev => ({
                ...prev,
                [name]: ''
            }));
        }
    };

    const selectCity = (city) => {
        setProperty(prev => ({
            ...prev,
            location: city
        }));
        setShowCities(false);
        if (formErrors.location) {
            setFormErrors(prev => ({
                ...prev,
                location: ''
            }));
        }
    };

    return (
        <Card>
            <CardHeader>
                <h2 className="text-xl font-semibold flex items-center gap-2">
                    <Plus className="h-5 w-5" />
                    List New Property
                </h2>
            </CardHeader>
            <CardContent>
                <form onSubmit={handleSubmit} className="space-y-4">
                    <div>
                        <Input
                            name="id"
                            placeholder="Property ID (letters, numbers, hyphens only)"
                            value={property.id}
                            onChange={handleInputChange}
                            required
                            disabled={isProcessing}
                            className={formErrors.id ? 'border-red-500' : ''}
                        />
                        {formErrors.id && (
                            <p className="text-red-500 text-sm mt-1">{formErrors.id}</p>
                        )}
                    </div>

                    <div>
                        <Input
                            name="title"
                            placeholder="Property Title (single line)"
                            value={property.title}
                            onChange={handleInputChange}
                            required
                            disabled={isProcessing}
                            className={formErrors.title ? 'border-red-500' : ''}
                        />
                        {formErrors.title && (
                            <p className="text-red-500 text-sm mt-1">{formErrors.title}</p>
                        )}
                    </div>

                    <div>
                        <Textarea
                            name="description"
                            placeholder="Property Description (max 4 lines)"
                            value={property.description}
                            onChange={handleInputChange}
                            required
                            disabled={isProcessing}
                            className={`min-h-[100px] ${formErrors.description ? 'border-red-500' : ''}`}
                            rows={4}
                        />
                        {formErrors.description && (
                            <p className="text-red-500 text-sm mt-1">{formErrors.description}</p>
                        )}
                    </div>

                    <div className="relative">
                        <div
                            className="relative"
                            onClick={() => !isProcessing && setShowCities(!showCities)}
                        >
                            <Input
                                name="location"
                                placeholder="Select Location"
                                value={property.location}
                                readOnly
                                required
                                disabled={isProcessing}
                                className={`cursor-pointer ${formErrors.location ? 'border-red-500' : ''}`}
                            />
                            <ChevronDown className="absolute right-3 top-3 h-4 w-4" />
                        </div>
                        {showCities && (
                            <div className="absolute z-10 w-full mt-1 bg-white border rounded-md shadow-lg max-h-60 overflow-auto">
                                {ISRAELI_CITIES.map((city) => (
                                    <div
                                        key={city}
                                        className="px-4 py-2 hover:bg-gray-100 cursor-pointer"
                                        onClick={() => selectCity(city)}
                                    >
                                        {city}
                                    </div>
                                ))}
                            </div>
                        )}
                        {formErrors.location && (
                            <p className="text-red-500 text-sm mt-1">{formErrors.location}</p>
                        )}
                    </div>

                    <div>
                        <Input
                            name="price"
                            type="number"
                            step="0.01"
                            placeholder="Price (ETH)"
                            value={property.price}
                            onChange={handleInputChange}
                            required
                            disabled={isProcessing}
                            className={formErrors.price ? 'border-red-500' : ''}
                        />
                        {formErrors.price && (
                            <p className="text-red-500 text-sm mt-1">{formErrors.price}</p>
                        )}
                    </div>

                    {formErrors.submit && (
                        <div className="text-red-500 text-sm">{formErrors.submit}</div>
                    )}

                    <Button 
                        type="submit" 
                        disabled={isProcessing || !contract}
                        className="w-full"
                    >
                        {isProcessing ? (
                            <div className="flex items-center justify-center gap-2">
                                <Loader2 className="h-4 w-4 animate-spin" />
                                Processing...
                            </div>
                        ) : 'List Property'}
                    </Button>
                </form>
            </CardContent>
        </Card>
    );
};

export default PropertyForm;
