import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Card, CardHeader, CardContent } from '../real-estate-package/components/ui/card';
import { Input } from '../real-estate-package/components/ui/input';
import { Button } from '../real-estate-package/components/ui/button';
import { Alert, AlertDescription } from '../real-estate-package/components/ui/alert';
import { ScrollArea } from '../real-estate-package/components/ui/scroll-area';
import {
    Building,
    Search,
    MapPin,
    DollarSign,
    ArrowLeft,
    AlertCircle,
    Loader2
} from 'lucide-react';

import { initializeWeb3, initializeContract, formatPrice } from '../real-estate-package/utilsApp/web3';

const PropertyListingsPage = () => {
    const navigate = useNavigate();
    const [properties, setProperties] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedLocation, setSelectedLocation] = useState('');
    const [priceRange, setPriceRange] = useState({ min: '', max: '' });
    const [filteredProperties, setFilteredProperties] = useState([]);

    const initializeAndLoadProperties = async () => {
        try {
            const web3 = await initializeWeb3();
            const contract = await initializeContract(web3);
            const results = await contract.methods.getAllProperties().call();

            const formattedProperties = results.map(property => ({
                ...property,
                price: formatPrice(web3, property.price)
            }));

            setProperties(formattedProperties);
            setFilteredProperties(formattedProperties);
        } catch (err) {
            setError('Failed to load properties. Please try again later.');
            console.error('Property loading error:', err);
        } finally {
            setIsLoading(false);
        }
    };

    useEffect(() => {
        initializeAndLoadProperties();
    }, []);

    useEffect(() => {
        let filtered = [...properties];

        // Apply search filter
        if (searchTerm) {
            filtered = filtered.filter(property =>
                property.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                property.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
                property.location.toLowerCase().includes(searchTerm.toLowerCase())
            );
        }

        // Apply location filter
        if (selectedLocation) {
            filtered = filtered.filter(property =>
                property.location === selectedLocation
            );
        }

        // Apply price range filter
        if (priceRange.min) {
            filtered = filtered.filter(property =>
                parseFloat(property.price) >= parseFloat(priceRange.min)
            );
        }
        if (priceRange.max) {
            filtered = filtered.filter(property =>
                parseFloat(property.price) <= parseFloat(priceRange.max)
            );
        }

        setFilteredProperties(filtered);
    }, [properties, searchTerm, selectedLocation, priceRange]);

    const locations = [...new Set(properties.map(prop => prop.location))].sort();

    if (isLoading) {
        return (
            <div className="flex items-center justify-center min-h-screen">
                <div className="text-center">
                    <Loader2 className="h-8 w-8 animate-spin mx-auto mb-4" />
                    <p className="text-gray-600">Loading properties...</p>
                </div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-gray-50 p-6">
            <Card className="max-w-7xl mx-auto">
                <CardHeader>
                    <div className="flex items-center justify-between mb-6">
                        <Button 
                            onClick={() => navigate('/menu')} 
                            variant="outline"
                            className="flex items-center gap-2"
                        >
                            <ArrowLeft className="h-4 w-4" />
                            Back to Menu
                        </Button>
                        <h1 className="text-2xl font-bold">Property Listings</h1>
                    </div>

                    <div className="space-y-4">
                        <div className="relative">
                            <Search className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
                            <Input
                                placeholder="Search properties..."
                                value={searchTerm}
                                onChange={(e) => setSearchTerm(e.target.value)}
                                className="pl-9"
                            />
                        </div>

                        <div className="flex flex-col gap-4 md:flex-row">
                            <select
                                value={selectedLocation}
                                onChange={(e) => setSelectedLocation(e.target.value)}
                                className="flex-1 p-2 border rounded-md"
                            >
                                <option value="">All Locations</option>
                                {locations.map(location => (
                                    <option key={location} value={location}>
                                        {location}
                                    </option>
                                ))}
                            </select>

                            <div className="flex items-center gap-2 flex-1">
                                <Input
                                    type="number"
                                    placeholder="Min Price (ETH)"
                                    value={priceRange.min}
                                    onChange={(e) => setPriceRange(prev => ({ ...prev, min: e.target.value }))}
                                />
                                <span>-</span>
                                <Input
                                    type="number"
                                    placeholder="Max Price (ETH)"
                                    value={priceRange.max}
                                    onChange={(e) => setPriceRange(prev => ({ ...prev, max: e.target.value }))}
                                />
                            </div>
                        </div>
                    </div>
                </CardHeader>

                <CardContent>
                    {error && (
                        <Alert variant="destructive" className="mb-6">
                            <AlertCircle className="h-4 w-4" />
                            <AlertDescription>{error}</AlertDescription>
                        </Alert>
                    )}

                    <ScrollArea className="h-[calc(100vh-300px)]">
                        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                            {filteredProperties.map((property, index) => (
                                <Card key={index} className="hover:shadow-lg transition-shadow">
                                    <div className="aspect-video bg-gray-100 flex items-center justify-center">
                                        <Building className="h-12 w-12 text-gray-400" />
                                    </div>
                                    <CardContent className="p-4">
                                        <h3 className="font-semibold text-lg mb-2">{property.title}</h3>
                                        <p className="text-sm text-gray-600 mb-4 line-clamp-2">
                                            {property.description}
                                        </p>
                                        <div className="space-y-2">
                                            <div className="flex items-center text-sm text-gray-500">
                                                <MapPin className="h-4 w-4 mr-2" />
                                                {property.location}
                                            </div>
                                            <div className="flex items-center text-sm font-medium">
                                                <DollarSign className="h-4 w-4 mr-2" />
                                                {property.price} ETH
                                            </div>
                                        </div>
                                        <Button className="w-full mt-4">View Details</Button>
                                    </CardContent>
                                </Card>
                            ))}
                        </div>

                        {filteredProperties.length === 0 && (
                            <div className="text-center py-12">
                                <Building className="h-12 w-12 mx-auto text-gray-400 mb-4" />
                                <h3 className="text-lg font-medium text-gray-900">No properties found</h3>
                                <p className="text-gray-500">Try adjusting your search criteria</p>
                            </div>
                        )}
                    </ScrollArea>
                </CardContent>
            </Card>
        </div>
    );
};

export default PropertyListingsPage;
