import React, { useState } from 'react';
import { Card, CardHeader, CardContent } from './card';
import { Button } from './button';
import { Input } from './input';
import { Textarea } from './textarea';
import { Plus, Loader2, ChevronDown, Image as ImageIcon } from 'lucide-react';
import {
    sanitizeInput,
    validateSecurity,
    validateFormSecurity,
    validatePropertyField,
    sanitizeAndValidateInput
} from '../../utilsApp/security';

const ISRAELI_CITIES = [
    'Jerusalem', 'Tel Aviv', 'Haifa', 'Rishon LeZion',
    'Petah Tikva', 'Ashdod', 'Netanya', 'Beer Sheva',
    'Holon', 'Bnei Brak'
];

const PropertyForm = ({ onSubmit, contract, isProcessing, web3Instance, account }) => {

    

    const [showCities, setShowCities] = useState(false);
    const [formErrors, setFormErrors] = useState({});
    const [property, setProperty] = useState({
        id: '',
        title: '',
        description: '',
        location: '',
        price: '',
        image: null
    });
    const [imagePreview, setImagePreview] = useState('');

    const [isSubmitting, setIsSubmitting] = useState(false);
    const [successMessage, setSuccessMessage] = useState('');


    const handleInputChange = (e) => {
        const { name, value } = e.target;
        
        let sanitizedValue = sanitizeAndValidateInput(value, 
            name === 'price' ? 'number' : 
            name === 'description' ? 'multiline' : 'text',
            name === 'id' ? 50 : 
            name === 'title' ? 60 : 
            name === 'description' ? 280 : undefined
        );

        if (name === 'description') {
            const lines = value.split('\n');
            if (lines.length > 4) {
                sanitizedValue = lines.slice(0, 4).join('\n');
            }
            if (sanitizedValue.length > 280) {
                sanitizedValue = sanitizedValue.substring(0, 280);
            }
        }

        if (name === 'price') {
            const numValue = parseFloat(sanitizedValue);
            if (numValue > 1000000) return;
        }

        setProperty(prev => ({
            ...prev,
            [name]: sanitizedValue
        }));

        const error = validatePropertyField(name, sanitizedValue);
        setFormErrors(prev => ({
            ...prev,
            [name]: error
        }));
    };
    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            if (file.size > 5242880) {
                setFormErrors(prev => ({
                    ...prev,
                    image: 'Image size should not exceed 5MB'
                }));
                return;
            }
    
            // Convert image to base64 string for blockchain storage
            const reader = new FileReader();
            reader.onloadend = () => {
                const imageData = reader.result.split(',')[1]; // Remove data URI prefix
                setProperty(prev => ({
                    ...prev,
                    image: imageData
                }));
                setImagePreview(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };
    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsSubmitting(true);
        setFormErrors({});
    
        try {
            if (!web3Instance || !account || !contract) {
                throw new Error('Blockchain connection not initialized. Please ensure your wallet is connected.');
            }
    
            // Basic validation
            if (!property.id?.trim() || !property.title?.trim() || !property.location?.trim() || !property.price) {
                throw new Error('Please fill in all required fields');
            }
    
            // Check if ID exists
            const properties = await contract.methods.getAllProperties().call();
            if (properties.some(prop => prop.id.toLowerCase() === property.id.toLowerCase())) {
                throw new Error('This Property ID already exists. Please choose a different one.');
            }
    
            // Image processing
            let processedImage = '';
            if (property.image) {
                // Compress and resize image
                const image = await compressImage(property.image);
                processedImage = image.slice(0, 500); // Store a truncated hash or reference
            }
    
            // Format price to Wei
            const priceInWei = web3Instance.utils.toWei(property.price.toString(), 'ether');
    
            // Estimate gas with smaller image data
            const gasEstimate = await contract.methods.createProperty(
                property.id,
                property.title,
                property.description,
                priceInWei,
                property.location,
                [processedImage] // Pass the processed image reference
            ).estimateGas({
                from: account
            });
    
            // Execute transaction with estimated gas
            const transaction = await contract.methods.createProperty(
                property.id,
                property.title,
                property.description,
                priceInWei,
                property.location,
                [processedImage]
            ).send({
                from: account,
                gas: Math.floor(gasEstimate * 1.2) // Add 20% buffer
            });
    
            if (transaction.status) {
                // Reset form
                setProperty({
                    id: '',
                    title: '',
                    description: '',
                    location: '',
                    price: '',
                    image: null
                });
                setImagePreview('');
                setSuccessMessage('Property listed successfully! Transaction hash: ' + transaction.transactionHash);
            }
    
        } catch (error) {
            console.error('Transaction error:', error);
            const errorMessage = error.message.toLowerCase();
            
            if (errorMessage.includes('gas')) {
                setFormErrors({
                    submit: 'Transaction requires too much gas. Please try with a smaller image or compress it further.'
                });
            } else if (errorMessage.includes('user denied')) {
                setFormErrors({
                    submit: 'Transaction was cancelled. Please try again if you want to list your property.'
                });
            } else {
                setFormErrors({
                    submit: error.message || 'Transaction failed. Please try again.'
                });
            }
        } finally {
            setIsSubmitting(false);
        }
    };
    
    // Helper function to compress images
    const compressImage = async (base64String) => {
        return new Promise((resolve) => {
            const img = new Image();
            img.onload = () => {
                const canvas = document.createElement('canvas');
                const MAX_WIDTH = 800;
                const MAX_HEIGHT = 600;
                
                let width = img.width;
                let height = img.height;
    
                if (width > height) {
                    if (width > MAX_WIDTH) {
                        height *= MAX_WIDTH / width;
                        width = MAX_WIDTH;
                    }
                } else {
                    if (height > MAX_HEIGHT) {
                        width *= MAX_HEIGHT / height;
                        height = MAX_HEIGHT;
                    }
                }
    
                canvas.width = width;
                canvas.height = height;
    
                const ctx = canvas.getContext('2d');
                ctx.drawImage(img, 0, 0, width, height);
    
                // Compress image to JPEG with reduced quality
                const compressedBase64 = canvas.toDataURL('image/jpeg', 0.6);
                resolve(compressedBase64);
            };
            img.src = base64String;
        });
    };



    const selectCity = (city) => {
        if (ISRAELI_CITIES.includes(city)) {
            setProperty(prev => ({
                ...prev,
                location: city
            }));
            setShowCities(false);
            setFormErrors(prev => ({
                ...prev,
                location: ''
            }));
        }
    };

    return (
        <Card>
            <CardHeader>
                <h2 className="text-xl font-semibold flex items-center gap-2">
                    <Plus className="h-5 w-5" />
                    List New Property
                </h2>
            </CardHeader>
            <CardContent>
                <form onSubmit={handleSubmit} className="space-y-4">
                    <div>
                        <Input
                            name="id"
                            placeholder="Property ID (letters, numbers, hyphens only)"
                            value={property.id}
                            onChange={handleInputChange}
                            required
                            disabled={isProcessing}
                            className={formErrors.id ? 'border-red-500' : ''}
                            maxLength={50}
                        />
                        {formErrors.id && (
                            <p className="text-red-500 text-sm mt-1">{formErrors.id}</p>
                        )}
                    </div>

                    <div>
                        <div className="border-2 border-dashed rounded-lg p-4 text-center hover:bg-gray-50 transition-colors">
                            <input
                                type="file"
                                accept="image/*"
                                onChange={handleImageChange}
                                className="hidden"
                                id="property-image"
                                disabled={isProcessing}
                            />
                            <label
                                htmlFor="property-image"
                                className="cursor-pointer flex flex-col items-center"
                            >
                                {imagePreview ? (
                                    <img
                                        src={imagePreview}
                                        alt="Property preview"
                                        className="max-h-48 object-contain mb-2 rounded-lg"
                                    />
                                ) : (
                                    <ImageIcon className="h-12 w-12 text-gray-400 mb-2" />
                                )}
                                <span className="text-sm text-gray-600">
                                    {imagePreview ? 'Change image' : 'Upload property image (max 5MB)'}
                                </span>
                            </label>
                        </div>
                        {formErrors.image && (
                            <p className="text-red-500 text-sm mt-1">{formErrors.image}</p>
                        )}
                    </div>

                    <div>
                        <Input
                            name="title"
                            placeholder="Property Title"
                            value={property.title}
                            onChange={handleInputChange}
                            required
                            disabled={isProcessing}
                            className={formErrors.title ? 'border-red-500' : ''}
                            maxLength={100}
                        />
                        {formErrors.title && (
                            <p className="text-red-500 text-sm mt-1">{formErrors.title}</p>
                        )}
                    </div>

                    <div>
                        <Textarea
                            name="description"
                            placeholder="Property Description (4 lines maximum)"
                            value={property.description}
                            onChange={handleInputChange}
                            required
                            disabled={isProcessing}
                            className={`min-h-[100px] resize-none ${formErrors.description ? 'border-red-500' : ''}`}
                            rows={4}
                            maxLength={400}
                            onKeyDown={(e) => {
                                const lines = e.target.value.split('\n');
                                if (e.key === 'Enter' && lines.length >= 4) {
                                    e.preventDefault();
                                }
                            }}
                        />
                        {formErrors.description && (
                            <p className="text-red-500 text-sm mt-1">{formErrors.description}</p>
                        )}
                    </div>

                    <div className="relative">
                        <div
                            className="relative cursor-pointer"
                            onClick={() => !isProcessing && setShowCities(!showCities)}
                        >
                            <Input
                                name="location"
                                placeholder="Select Location"
                                value={property.location}
                                readOnly
                                required
                                disabled={isProcessing}
                                className={formErrors.location ? 'border-red-500' : ''}
                            />
                            <ChevronDown className="absolute right-3 top-3 h-4 w-4" />
                        </div>
                        {showCities && (
                            <div className="absolute z-10 w-full mt-1 bg-white border rounded-md shadow-lg max-h-60 overflow-auto">
                                {ISRAELI_CITIES.map((city) => (
                                    <div
                                        key={city}
                                        className="px-4 py-2 hover:bg-gray-100 cursor-pointer"
                                        onClick={() => selectCity(city)}
                                    >
                                        {city}
                                    </div>
                                ))}
                            </div>
                        )}
                        {formErrors.location && (
                            <p className="text-red-500 text-sm mt-1">{formErrors.location}</p>
                        )}
                    </div>

                    <div>
                        <Input
                            name="price"
                            type="number"
                            step="0.01"
                            min="0"
                            max="1000000"
                            placeholder="Price (ETH)"
                            value={property.price}
                            onChange={handleInputChange}
                            required
                            disabled={isProcessing}
                            className={formErrors.price ? 'border-red-500' : ''}
                        />
                        {formErrors.price && (
                            <p className="text-red-500 text-sm mt-1">{formErrors.price}</p>
                        )}
                    </div>

                    {formErrors.submit && (
                        <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded">
                            {formErrors.submit}
                        </div>
                    )}

                    <Button 
                        type="submit" 
                        disabled={isProcessing || !contract}
                        className="w-full"
                    >
                        {isProcessing ? (
                            <div className="flex items-center justify-center gap-2">
                                <Loader2 className="h-4 w-4 animate-spin" />
                                Processing...
                            </div>
                        ) : 'List Property'}
                    </Button>
                </form>
            </CardContent>
        </Card>
    );
};

export default PropertyForm;
