import React, { useState } from 'react';
import { Card, CardHeader, CardContent } from './card';
import { Button } from './button';
import { Input } from './input';
import { Textarea } from './textarea';
import { Plus, Loader2, ChevronDown } from 'lucide-react';
import { validatePropertyFormData } from '../../utilsApp/errors';
import { displayErrorMessage } from '../../utilsApp/errors';

const ISRAELI_CITIES = [
    'Jerusalem', 'Tel Aviv', 'Haifa', 'Rishon LeZion',
    'Petah Tikva', 'Ashdod', 'Netanya', 'Beer Sheva',
    'Holon', 'Bnei Brak'
];

const PropertyForm = ({ web3Instance, contract, account, onPropertyCreated }) => {
    const [isProcessing, setIsProcessing] = useState(false);
    const [showCities, setShowCities] = useState(false);
    const [formErrors, setFormErrors] = useState({});
    const [property, setProperty] = useState({
        id: '',
        title: '',
        description: '',
        location: '',
        price: ''
    });

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsProcessing(true);
        setFormErrors({});

        try {
            // Validate form data
            validatePropertyFormData(property);

            // Basic connection checks
            if (!contract || !account || !web3Instance) {
                throw new Error('Please ensure your wallet is connected');
            }

            // Convert price to Wei
            const priceInWei = web3Instance.utils.toWei(property.price, 'ether');

            // Get gas estimate first
            const gasEstimate = await contract.methods.createProperty(
                property.id,
                property.title,
                property.description,
                priceInWei,
                property.location,
                [] // Empty documents array for now
            ).estimateGas({ from: account });

            // Add 20% buffer to gas estimate
            const gasLimit = Math.ceil(gasEstimate * 1.2);

            // Get current gas price
            const gasPrice = await web3Instance.eth.getGasPrice();

            // Execute the transaction with specific parameters
            const transaction = await contract.methods.createProperty(
                property.id,
                property.title,
                property.description,
                priceInWei,
                property.location,
                [] // Empty documents array
            ).send({
                from: account,
                gas: gasLimit,
                gasPrice: gasPrice
            });

            // Wait for transaction receipt
            const receipt = await web3Instance.eth.getTransactionReceipt(transaction.transactionHash);
            
            if (!receipt.status) {
                throw new Error('Transaction failed. Please check the transaction on block explorer.');
            }

            // Reset form after successful submission
            setProperty({
                id: '',
                title: '',
                description: '',
                location: '',
                price: ''
            });

            // Notify parent component
            if (onPropertyCreated) {
                onPropertyCreated();
            }

        } catch (error) {
            console.error('Property Creation Error:', error);
            const errorMessage = displayErrorMessage(error, 'Property Creation Error');
            setFormErrors({ submit: errorMessage });
        } finally {
            setIsProcessing(false);
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setProperty(prev => ({
            ...prev,
            [name]: value
        }));
        // Clear error for this field when user starts typing
        if (formErrors[name]) {
            setFormErrors(prev => {
                const newErrors = { ...prev };
                delete newErrors[name];
                return newErrors;
            });
        }
    };

    const selectCity = (city) => {
        setProperty(prev => ({
            ...prev,
            location: city
        }));
        setShowCities(false);
    };

    const buttonDisabled = isProcessing || !contract || !account;

    return (
        <Card>
            <CardHeader>
                <h2 className="text-xl font-semibold flex items-center gap-2">
                    <Plus className="h-5 w-5" />
                    List New Property
                </h2>
            </CardHeader>
            <CardContent>
                {formErrors.submit && (
                    <div className="mb-4 p-3 rounded bg-red-50 text-red-600 text-sm">
                        {formErrors.submit}
                    </div>
                )}

                <form onSubmit={handleSubmit} className="space-y-4">
                    <div>
                        <Input
                            name="id"
                            placeholder="Property ID"
                            value={property.id}
                            onChange={handleInputChange}
                            className={formErrors.id ? 'border-red-500' : ''}
                            disabled={isProcessing}
                        />
                        {formErrors.id && (
                            <p className="text-red-500 text-sm mt-1">{formErrors.id}</p>
                        )}
                    </div>

                    <div>
                        <Input
                            name="title"
                            placeholder="Property Title"
                            value={property.title}
                            onChange={handleInputChange}
                            className={formErrors.title ? 'border-red-500' : ''}
                            disabled={isProcessing}
                        />
                        {formErrors.title && (
                            <p className="text-red-500 text-sm mt-1">{formErrors.title}</p>
                        )}
                    </div>

                    <div>
                        <Textarea
                            name="description"
                            placeholder="Property Description"
                            value={property.description}
                            onChange={handleInputChange}
                            className={`min-h-[100px] ${formErrors.description ? 'border-red-500' : ''}`}
                            disabled={isProcessing}
                        />
                        {formErrors.description && (
                            <p className="text-red-500 text-sm mt-1">{formErrors.description}</p>
                        )}
                    </div>

                    <div className="relative">
                        <div className="flex gap-2">
                            <Input
                                name="location"
                                placeholder="Location"
                                value={property.location}
                                onChange={handleInputChange}
                                className={formErrors.location ? 'border-red-500' : ''}
                                disabled={isProcessing}
                            />
                            <Button
                                type="button"
                                variant="outline"
                                onClick={() => setShowCities(!showCities)}
                                disabled={isProcessing}
                                className="flex items-center gap-2"
                            >
                                <ChevronDown className={`h-4 w-4 transform ${showCities ? 'rotate-180' : ''}`} />
                            </Button>
                        </div>
                        {showCities && (
                            <div className="absolute z-10 w-full mt-1 bg-white border rounded-md shadow-lg">
                                {ISRAELI_CITIES.map((city) => (
                                    <button
                                        key={city}
                                        type="button"
                                        className="w-full px-4 py-2 text-left hover:bg-gray-100"
                                        onClick={() => selectCity(city)}
                                    >
                                        {city}
                                    </button>
                                ))}
                            </div>
                        )}
                        {formErrors.location && (
                            <p className="text-red-500 text-sm mt-1">{formErrors.location}</p>
                        )}
                    </div>

                    <div>
                        <Input
                            name="price"
                            type="number"
                            step="0.01"
                            placeholder="Price (ETH)"
                            value={property.price}
                            onChange={handleInputChange}
                            className={formErrors.price ? 'border-red-500' : ''}
                            disabled={isProcessing}
                        />
                        {formErrors.price && (
                            <p className="text-red-500 text-sm mt-1">{formErrors.price}</p>
                        )}
                    </div>

                    <Button 
                        type="submit" 
                        disabled={buttonDisabled}
                        className="w-full"
                    >
                        {isProcessing ? (
                            <div className="flex items-center justify-center gap-2">
                                <Loader2 className="h-4 w-4 animate-spin" />
                                Processing Transaction...
                            </div>
                        ) : !account ? (
                            'Connect Wallet to List Property'
                        ) : (
                            'List Property'
                        )}
                    </Button>
                </form>
            </CardContent>
        </Card>
    );
};

export default PropertyForm;
